import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link, Switch, Redirect } from 'react-router-dom';

function Signup() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:5000/api/signup', { username, password });
      setUsername('');
      setPassword('');
      setError('');
    } catch (error) {
      setError('Failed to sign up');
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h2 className="mt-4 mb-3">Signup</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={handleSignup}>
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Username</label>
              <input
                type="text"
                id="username"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input
                type="password"
                id="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Signup</button>
          </form>
        </div>
      </div>
    </div>
  );
}

function Login({ setLoggedIn }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/login', { username, password });
      localStorage.setItem('token', response.data.token);
      setUsername('');
      setPassword('');
      setError('');
      setLoggedIn(true);
    } catch (error) {
      setError('Invalid login credentials');
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h2 className="mt-4 mb-3">Login</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={handleLogin}>
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Username</label>
              <input
                type="text"
                id="username"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input
                type="password"
                id="password"
                className="form-control"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Login</button>
          </form>
        </div>
      </div>
    </div>
  );
}

function Home() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/posts');
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts', error);
    }
  };

  return (
    <div className="container">
      <h2 className="mt-4 mb-3">Blog</h2>
      {posts.map((post) => (
        <div key={post._id} className="card mb-3">
          <div className="card-body">
            <h3 className="card-title">{post.title}</h3>
            <p className="card-text">Author: {post.author.username}</p>
            <Link to={`/posts/${post._id}`} className="btn btn-primary">Read More</Link>
          </div>
        </div>
      ))}
    </div>
  );
}

function AddPost() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState('');

  const handleAddPost = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/posts',
        { title, content },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setTitle('');
      setContent('');
      setError('');
    } catch (error) {
      setError('Failed to add post');
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h2 className="mt-4 mb-3">Add Post</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={handleAddPost}>
            <div className="mb-3">
              <label htmlFor="title" className="form-label">Title</label>
              <input
                type="text"
                id="title"
                className="form-control"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="content" className="form-label">Content</label>
              <textarea
                id="content"
                className="form-control"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
              ></textarea>
            </div>
            <button type="submit" className="btn btn-primary">Add Post</button>
          </form>
        </div>
      </div>
    </div>
  );
}

function SinglePost({ match }) {
  const [post, setPost] = useState(null);

  useEffect(() => {
    fetchPost();
  }, []);

  const fetchPost = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/posts/${match.params.id}`);
      setPost(response.data);
    } catch (error) {
      console.error('Error fetching post', error);
    }
  };

  const handleDeletePost = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/posts/${match.params.id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setPost(null);
    } catch (error) {
      console.error('Error deleting post', error);
    }
  };

  return (
    <div className="container">
      {post ? (
        <div>
          <h2 className="mt-4 mb-3">{post.title}</h2>
          <p className="mb-3">Author: {post.author.username}</p>
          <p>{post.content}</p>
          {post.image && <img src={post.image} alt="Post" className="img-fluid mb-3" />}
          <button onClick={handleDeletePost} className="btn btn-danger">Delete Post</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

function PrivateRoute({ component: Component, isLoggedIn, ...rest }) {
  return (
    <Route
      {...rest}
      render={(props) => (
        isLoggedIn
          ? <Component {...props} />
          : <Redirect to="/login" />
      )}
    />
  );
}

function App() {
  const [isLoggedIn, setLoggedIn] = useState(!!localStorage.getItem('token'));

  const handleLogout = () => {
    localStorage.removeItem('token');
    setLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container">
            <Link to="/" className="navbar-brand">Blog</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
                <li className="nav-item">
                  <Link to="/" className="nav-link">Home</Link>
                </li>
                {isLoggedIn ? (
                  <>
                    <li className="nav-item">
                      <Link to="/add" className="nav-link">Add Post</Link>
                    </li>
                    <li className="nav-item">
                      <button onClick={handleLogout} className="btn btn-link nav-link">Logout</button>
                    </li>
                  </>
                ) : (
                  <>
                    <li className="nav-item">
                      <Link to="/signup" className="nav-link">Signup</Link>
                    </li>
                    <li className="nav-item">
                      <Link to="/login" className="nav-link">Login</Link>
                    </li>
                  </>
                )}
              </ul>
            </div>
          </div>
        </nav>

        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/signup" component={Signup} />
          <Route path="/login" render={(props) => <Login setLoggedIn={setLoggedIn} {...props} />} />
          <PrivateRoute path="/add" component={AddPost} isLoggedIn={isLoggedIn} />
          <Route path="/posts/:id" component={SinglePost} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
