import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function SinglePost() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);

  useEffect(() => {
    fetchPost();
  });

  const fetchPost = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/posts/${id}`);
      setPost(response.data);
    } catch (error) {
      console.error('Error fetching post', error);
    }
  };

  const handleDeletePost = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/posts/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setPost(null);
      // Navigate to home page after successful post deletion
      navigate('/');
    } catch (error) {
      console.error('Error deleting post', error);
    }
  };

  return (
    <div className="container">
      {post ? (
        <div>
          <h2 className="mt-4 mb-3">{post.title}</h2>
          <p className="mb-3">Author: {post.content}</p>
          <p>{post.content}</p>
          <button onClick={handleDeletePost} className="btn btn-danger">Delete Post</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default SinglePost;
